import React, { useEffect, useState } from 'react'
import { Form, Formik } from 'formik'
import * as Yup from 'yup'
import FormikControl from "../FormComponents/FormikControl"
import { Button } from '@chakra-ui/react'
import './LoginForm.scss'
import { Container } from 'react-bootstrap'
import { useHistory } from 'react-router-dom'
import loginSvg from './login.svg'
//import secureLoginSvg from './secure-login.svg'


function LoginForm({ isLoggedIn, setIsLoggedIn, setLoggedUser }) {
    const history = useHistory();
    const initialValues = {
        email: '',
        password: '',
        loggedUser: 'Sefa'
    }
    const validationSchema = Yup.object({
        email: Yup.string().email('Invalid Format').required('Required'),
        password: Yup.string().required('Required')
    })

    useEffect(() => {
        return () => {
            history.push("/");
        }
    }, [isLoggedIn, history])


    const [isSubmitting, setIsSubmitting] = useState(false)
    const onSubmit = (e) => { //  todo  ! e variables ı bütün girilenleri tutuyor. eyi al backende yolla
        try {
            console.log('form date', e)
            if (e.email === "aydogan15@itu.edu.tr") {
                if (e.password === "asdasd") {
                    setIsSubmitting(true)
                    setTimeout(() => {
                        setIsSubmitting(false)
                        setIsLoggedIn(true);
                        setLoggedUser(initialValues.loggedUser);
                    }, 1000);
                }
                else {
                    console.log('password is incorrect')
                }
            }
            else {
                console.log('email is incorrect')
            }
        } catch (e) {
            alert(e.message);
        }
    }

    return (
        <div className="loginPage">
            <Container>
                <div className="loginForm">
                    <div className="leftPanel">
                        <img src={loginSvg} alt="Login Page" />
                    </div>
                    <div className="rightPanel">
                        <Formik
                            initialValues={initialValues}
                            validationSchema={validationSchema}
                            onSubmit={onSubmit}
                        >
                            {
                                formik => {
                                    return (
                                        <div>
                                            <div className="loginHeader">
                                                <h1>Giriş Yap</h1>
                                            </div>
                                            <Form>
                                                <FormikControl
                                                    control='chakrainput'
                                                    type='email'
                                                    label='Email'
                                                    name='email'
                                                    placeholder='birisi@example.com'
                                                />
                                                <FormikControl
                                                    control='chakrainput'
                                                    type='password'
                                                    label='Password'
                                                    name='password'
                                                />
                                                <Button
                                                    variant="outline"
                                                    isLoading={isSubmitting}
                                                    loadingText="Submitting"
                                                    bg="white.400"
                                                    textColor="white"
                                                    type='submit'
                                                    disabled={!formik.isValid}
                                                >
                                                    Giriş Yap
                                                </Button>
                                            </Form>
                                        </div>
                                    )
                                }
                            }
                        </Formik>
                    </div>
                </div>
            </Container>
        </div>
    )
}
export default LoginForm
